var app=angular.module("image",[]);app.controller("ImageCtrl",function(e,t){localStorage.removeItem("images");e.imgs=[];var n=localStorage.getItem("images");n!=""&&n!=undefined&&(e.imgs=angular.fromJson(n));e.setAlert=function(){e.alert=!0;e.imgs.length>0&&(e.alert=!1)};e.snap=function(){var n={destinationType:Camera.DestinationType.FILE_URI,sourceType:Camera.PictureSourceType.PHOTOLIBRARY};t.getPicture(n).then(function(t){e.imgs.push({id:(new Date).getTime(),url:t});localStorage.setItem("images",angular.toJson(e.imgs))},function(e){console.log(e)})};e.setAlert()});